mapper.update:
update post 
set p_thumbnail=#{param2}
where p_idx=#{param1}

mapper.select:
select count(*) from post 
where member_idx=#{param1}
and p_loc=#{param2}

mapper.update:
update post 
set p_title=#{p_title}, p_content=#{p_content}, p_loc=#{p_loc}
where p_idx=#{p_idx}

update post 
set p_likes=p_likes+#{param2}
where p_idx=#{param1}
